import { useContext } from "react";
import { GithubContext } from "../providers/GithubProviders";

const useGithub = () => {
  // eslint-disable-next-line no-unused-vars
  const { githubState, getUser } = useContext( GithubContext );

  return {githubState};
};

export default useGithub
