  user: {},
  repositories: [],
  starred: [],
});

const GithubProviders = ({ children }) => {

  const [githubState, useGithubState] = useState({
    user: {
      login: undefined,
      name: undefined,
      html_url: undefined,
      blog: undefined,
      company: undefined,
      location: undefined,
      followers: 0,
      following: 0,
      publicGists: 0,
      publicRepos: 0,      
