import styled from 'styled-components';

export const Wrapper = styled.div`
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 8px;
  margin: 16px 16px;
  width: 29%;
  height: 200px;
  align-content: center;

  a{
    margin-top: 20px;
  }
`;

export const WrapperTitle = styled.h2`
  font-size: 20px;
  font-weight: bold;
  margin: 6px 0 20px 0;
`;
