import styled from 'styled-components';

export const Wrapper = styled.div`
  display: flex;
  align-items: flex-start;
`;

export const WrapperUserInfo = styled.div`
  display: flex;
  flex-direction: column; 
  align-items: flex-start;
  justify-content: space-evenly;
  width: 100%;
  margin-left: 15px;
  height: 235px;

  h1 {
    font-size: 28px;
    font-weight: bold;
  }
  h3 {
    font-size: 18px;
  }
  h4 {
    font-size: 16px;
  }
`;

export const WrapperStatusCount = styled.div`
  display: flex;
  align-items: center;
  div {
    margin: 8px;
    text-align: center;
  }
`;

export const WrapperUserName = styled.div`
  display: flex;
  align-items: center;
  h3 {
    margin-right: 5px;
  }

  a {
    color: blue;
    font-weight: bold;
  }
`;

export const WrapperImage = styled.img`
  border-radius: 50%;
  width: 210px;
  margin: 12px
`;
