import styled from 'styled-components';

export const Wrapper = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
  padding: 10px;

  input {
    border: 3px solid #ccc;
    border-radius: 8px;
    width: 100%;
    height: 100%;
    padding: 5px;
    margin-right: 20px;
    margin-top: 4px;
  }

  button {
    background-color: #ddd;
    padding: 10px;
    border: 1px solid #000;
    border-radius: 15%;
    margin
  }
`;
